    "MetaCorrBase",
    inherit = jmvcore::Analysis,
    public = list(
        initialize = function(options, data=NULL, datasetId="", analysisId="", revision=0) {
            super$initialize(
                package = 'MAJOR',
                name = 'MetaCorr',
                version = c(1,0,2),
                options = options,
                results = MetaCorrResults$new(options=options),
                data = data,
                datasetId = datasetId,
                analysisId = analysisId,
                revision = revision,
                pause = NULL,
                completeWhenFilled = FALSE)
        }))

#' Correlation Coefficients
#'
#' 
#' @param data .
#' @param rcor .
#' @param samplesize .
#' @param slab .
#' @param moderatorcor .
#' @param methodmetacor .
#' @param cormeasure .
#' @param moderatorType .
#' @param level .
#' @param showModelFit .
#' @param addcred .
#' @param addfit .
#' @param showweights .
#' @param steps .
#' @param xAxisTitle .
#' @param pchForest .
#' @param forestOrder .
#' @param fsntype .
#' @param yaxis .
#' @param yaxisInv .
#' @param enhanceFunnel .
#' @param lowerTOST .
#' @param upperTOST .
#' @param alphaTOST .
#' @param showTestTOST .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$textRICH} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$tableTauSqaured} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$modelFitRICH} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$pubBias$fsnRICH} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$pubBias$rankRICH} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$pubBias$regRICH} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$pubBias$TOSToutput} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$pubBias$TOSToutputtext} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$tostplot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$funplot} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$textRICH$asDF}
#'
#' \code{as.data.frame(results$textRICH)}
#'
#' @export
MetaCorr <- function(
    data,
    rcor,
    samplesize,
    slab,
    moderatorcor,
    methodmetacor = "REML",
    cormeasure = "ZCOR",
    moderatorType = "NON",
    level = 95,
    showModelFit = FALSE,
    addcred = FALSE,
    addfit = TRUE,
    showweights = FALSE,
    steps = 5,
    xAxisTitle,
    pchForest = "15",
    forestOrder = "fit",
    fsntype = "Rosenthal",
    yaxis = "sei",
    yaxisInv = FALSE,
    enhanceFunnel = FALSE,
    lowerTOST = -0.5,
    upperTOST = 0.5,
    alphaTOST = 0.05,
    showTestTOST = TRUE) {

    if ( ! requireNamespace('jmvcore'))
        stop('MetaCorr requires jmvcore to be installed (restart may be required)')

    if ( ! missing(rcor)) rcor <- jmvcore::resolveQuo(jmvcore::enquo(rcor))
    if ( ! missing(samplesize)) samplesize <- jmvcore::resolveQuo(jmvcore::enquo(samplesize))
    if ( ! missing(slab)) slab <- jmvcore::resolveQuo(jmvcore::enquo(slab))
    if ( ! missing(moderatorcor)) moderatorcor <- jmvcore::resolveQuo(jmvcore::enquo(moderatorcor))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(rcor), rcor, NULL),
            `if`( ! missing(samplesize), samplesize, NULL),
            `if`( ! missing(slab), slab, NULL),
            `if`( ! missing(moderatorcor), moderatorcor, NULL))


    options <- MetaCorrOptions$new(
        rcor = rcor,
        samplesize = samplesize,
        slab = slab,
        moderatorcor = moderatorcor,
        methodmetacor = methodmetacor,
        cormeasure = cormeasure,
        moderatorType = moderatorType,
        level = level,
        showModelFit = showModelFit,
        addcred = addcred,
        addfit = addfit,
        showweights = showweights,
        steps = steps,
        xAxisTitle = xAxisTitle,
        pchForest = pchForest,
        forestOrder = forestOrder,
        fsntype = fsntype,
        yaxis = yaxis,
        yaxisInv = yaxisInv,
        enhanceFunnel = enhanceFunnel,
        lowerTOST = lowerTOST,
        upperTOST = upperTOST,
        alphaTOST = alphaTOST,
        showTestTOST = showTestTOST)

    analysis <- MetaCorrClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}
